create database coursach
use coursach

create table Admin(
    Name varchar(40) not null primary key,
	Password varchar(100) not null,
)

create table User_(
    Username varchar(40) not null primary key,
	Password varchar(100) not null
)

create table Era(
    Id int primary key,
	PicturePath varchar(300),
    Name varchar(100),
	ShortDescription varchar(200),
    Description varchar(1500),
	Category varchar(100)
)

create table Travel(
	Id int primary key,
	Username varchar(40) foreign key references User_(Username),
	EraId int foreign key references Era(id),
)

create table Feedback
(
	Id int primary key,
	Username varchar(40) foreign key references User_(Username),
	Description varchar(400),
)

use coursach

insert into Admin
values 
('admin', 'admin')

insert into Era
values
(1, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\EarlyPaleolit.jpg', 'Ранний Палеолит', 'Начало нашей истории', 'Ранний палеолит — период в истории человечества, начавшийся с конца эпохи плиоцена, в котором началось первое использование каменных орудий предками современного человека Homo habilis. Это были сравнительно простые инструменты, известные как колуны, сфероиды (грубо оббитые камни) и отщепы. Homo habilis освоили каменные орудия в эпоху олдувайской культуры, которые использовались в качестве рубил и каменных сердечников. Живущие в эту эпоху люди существовали преимущественно за счет мяса павших животных и собирательства дикорастущих растений, так как охота в это время не была ещё распространена.
Около 1,5 млн лет назад появился более развитый человеческий род Homo erectus. Представители данного вида (синантропы) научились использовать огонь и создавали более сложные рубящие инструменты из камня, а также расширили свой ареал за счёт освоения Азии, что подтверждается находками на плато Чжоукоудянь в Китае. Около 1 млн лет назад человек заселил Европу и начал использовать каменные топоры.
Социальная организация людей находилась на стадии первобытного стада (неустойчивое, формировалось с целью охоты, для защиты от врагов, животных, стихии). Орудия работы — в целом наиболее примитивные каменные, деревянные, костяные инструменты. Хозяйственная деятельность заключалась в собирательстве и охоте, люди вели кочевой образ жизни.
Последний период нижнего палеолита, который длился от 337 000 до 300 000 лет назад.', 'Первобытный мир'),
(2, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\MiddlePaleolit.jpg', 'Средний Палеолит', 'Второй этап', 'Средний палеолит — эпоха, длившаяся в период от 200 000 до 40 000 лет назад. Более точная датировка существующими методами затруднена. Характеризуется широким расселением человека в результате которого палеоантроп (человек среднего палеолита) расселился практически по всей свободной от ледника территории Европы. Значительно выросло количество археологических памятников. Территория в Европе заселяется до Волги. Cтоянки появляются в бассейне Десны, верховьях Оки, Среднем Поволжье. В Центральной и Восточной Европе памятников среднего палеолита в 70 раз больше, чем раннего палеолита. Одновременно появляются местные группы и культуры, что становится основой для рождения новых рас и народов.
Усовершенствовалось производство каменных орудий труда. Каменная индустрия того времени носит название «леваллуа». Она характеризуется сколом отщепов и пластин со специально подготовленного дисковидного «нуклеуса». Они отличаются стойкостью форм. Двусторонне обработанные орудия труда в отдельных регионах используются и в среднем палеолите, но существенно изменяются. Ручные рубила уменьшаются в размерах, часто изготовляются из отщепов. Появляются листоподобные наконечники и наконечники разных типов, которые использовались в сложных орудиях труда и оружии, например в метательных копьях. Типичное орудие труда мустье — скребло — имеет многолезвенные формы. Мустьерские орудия труда многофункциональны: они служили для обработки дерева и шкур, строгания, резания и даже сверления.', 'Первобытный мир'),
(3, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\LatePaleolit.jpg', 'Поздний Палеолит', 'Конец первобытного мира', 'Около 50 000 лет назад древняя каменная индустрия изменилась. Вместо одной-двух археологических культур, сходных на всех континентах, появилось множество разнообразных культур и артефактов. Сначала в Африке, а затем и в других регионах кроме каменных появились изделия из кости и рога, а также значительно увеличилось количество произведений древнего искусства. Судя по находкам в южноафриканской пещере Бломбос, в эту эпоху люди научились ловить рыбу. В течение последующих 3—4 тысяч лет новые технологии распространились и в Европе, что привело к резкому росту численности населения и, возможно, стало причиной вытеснения и вымирания неандертальцев, конкурировавших с мигрантами из Африки. Пришельцы, которых принято называть кроманьонцами, по названию скального грота Кро-Маньон во французском селении Ле-Эзи-де-Таяк-Сирёй.
Важнейшим событием в развитии охотничьего вооружения явилось изобретение первого механического приспособления для метания дротиков — копьеметалки. Появились разнообразные каменные орудия для разделывания туш и обработки шкур добытых животных.
Искусство позднего палеолита включает многочисленные фигуры палеолитических Венер, художественную резьбу и гравюры животных на кости и роге, пещерную живопись, петроглифы, а также появление экзотических материалов далеко от места их происхождения, что позволяет предположить наличие навыков торгового обмена. Усложняется структура общества, входит в употребление символика и ритуалы, обозначающие групповую идентичность.', 'Первобытный мир'),
(4, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\AncientGreece.jpg', 'Древняя Греция', 'Краткая история Древней Греции', 'История Древней Греции является одной из составных частей истории древнего мира, изучающей состояние классовых обществ и государств, которые возникли и развивались в странах Древнего Востока и Средиземноморья. История Древней Греции изучает возникновение, расцвет и падение общественных и государственных структур, которые образовались на территории Балканского полуострова и в Эгейском регионе, в Южной Италии, на о. Сицилия и в Причерноморье. Она начинается с рубежа III—II тысячелетий до н. э. —с возникновения первых государственных образований на острове Крит, а заканчивается во II—I вв. до н. э., когда греческие и эллинистические государства Восточного Средиземноморья были захвачены Римом и включены в состав Римской средиземноморской державы.
За двухтысячелетний период истории древние греки создали рациональную экономическую систему, основанную на экономном использовании трудовых и природных ресурсов, гражданскую общественную структуру, полисную организацию с республиканским устройством, высокую культуру, оказавшую огромное воздействие на развитие римской и мировой культуры. Эти достижения древнегреческой цивилизации обогатили мировой исторический процесс, послужили фундаментом для последующего развития народов Средиземноморья в эпоху римского господства.', 'Древний мир'),
(5, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\AncientEast.jpg', 'Древний Восток', 'Краткая история Древнего Востока', 'Возникновение первых цивилизаций в Шумере и Египте (вторая половина IV тысячелетия до н. э.) резко изменило всю картину древности. В бескрайнем мире первобытных племен появились высокоразвитые центры. Взаимодействие раннеклассовых обществ с их окружением было сложным и многообразным.
Основные центры древневосточных цивилизаций сложились в долинах великих рек — Нила, Евфрата и Тигра, Инда и Ганга, Хуанхэ. Необходимость регулирования сложного режима этих рек определила некоторые общие черты в организации производства, известное единство древневосточного мира.
Централизованная экономика, широкий спектр отношений зависимости, низведение юридически свободных и полусвободных лиц до положения невольников деспотической власти фараона, постоянное вмешательство государства во все сферы жизни и хозяйства —все это можно видеть в Древнем Египте.
Государственное и частное землевладение, частный сектор в экономике, сильные в хозяйственном и политическом отношении города, с которыми приходилось считаться вавилонским царям, наличие границ, открытых для вторжения в долину Двуречья воинственным соседям, характерны для общества Месопотамии.
Варново-кастовая система, раздробленность, приведшая к сосуществованию различных форм политической организации — от монархии до республиканских образований при наличии культурного единства страны,—отличают цивилизацию Древней Индии от соседнего Китая с его оригинальной системой социальных рангов и деспотическим централизованным государством.', 'Древний мир'),
(6, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\AncientRome.jpg', 'Древний Рим', 'Один из лучших', 'Древний Рим — одна из самых развитых цивилизаций Древнего мира, государство Античности, получила своё название по главному городу (Roma — Рим), в свою очередь названному в честь легендарного основателя — Ромула. Центр Рима сложился в пределах болотистой равнины, ограниченной Капитолием, Палатином и Квириналом. Определённое влияние на становление древнеримской цивилизации оказали культуры этрусков и древних греков. Пика своего могущества Древний Рим достиг во II веке н. э., когда под его контролем оказалось пространство от современной Англии на севере до Судана на юге и от Ирака на востоке до Португалии на западе.
Официальным языком древнеримского государства был латинский. Неофициальным символом — гербом империи был Золотой орёл, после принятия христианства появились лабарумы (знамя, установленное императором Константином для своих войск) с хризмой (монограмма Иисуса Христа — скрещённые буквы Хи и Ро). Религия в Древнем Риме в течение большей части периода существования была политеистична. Христианство как религия зародилось на территории оккупированной Римской империей Палестины.
Современному миру цивилизация Древнего Рима подарила римское право, некоторые архитектурные формы и решения (например, арку и купол), каменные дороги и множество других новшеств (например, колёсные водяные мельницы).', 'Древний мир'),
(7, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\EarlyMiddleAge.jpg', 'Раннее Средневековье', 'Длился около шести веков', 'Раннее Средневековье — период европейской истории, начавшийся после падения Западной Римской империи. Длился около шести веков, приблизительно с 476 по 1100 год. В эпоху раннего Средневековья произошло Великое переселение народов, появились викинги, возникли королевства остготов в Италии и вестготов в Аквитании и на Пиренейском полуострове и образовалось Франкское государство, в период своего расцвета занимавшее большую часть Западной Европы. Северная Африка и Испания вошли в состав Арабского халифата, на Британских островах существовало множество небольших государств англов, саксов и кельтов, появились государства в Скандинавии, а также в центральной и восточной Европе: Великая Моравия и Киевская Русь.', 'Средние века'),
(8, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\HighMiddleAge.jpg', 'Высокое Средневековье', 'Приблизительно XI—XIV века', 'Для Средневековья это был период самого бурного развития. Увеличение численности населения повлекло за собой существенные изменения повсеместно в Европе. Численность населения росла, возделывались все новые поля, строились новые города, новое дыхание получили торговля и экономика, стали применяться новые технические изобретения. Существенно возросло влияние римского епископа, или папы, а также мощь католической церкви. Одновременно с этим высокое Средневековье было периодом крестовых походов, а также расцвета рыцарского сословия и вассально-ленной системы. В этот период сформировались границы современной Европы. Благодаря присоединению новых земель (в том числе и Эстонии) территория Европы за три столетия увеличилась примерно вдвое. В Северной и Восточной Европе образовались новые королевства такие, как Швеция, Норвегия, Дания, Польша, Венгрия. Существенный прогресс произошел в сфере образования – возникли домские и монастырские школы, были основаны первые университеты.', 'Средние века'),
(9, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\LateMiddleAge.jpg', 'Позднее Средневековье', 'XIV–XV века', 'Голод и обширная эпидемия чумы в XIV веке стали причиной небывалой демографической катастрофы в Европе. Огромные человеческие потери принесла с собой также Столетняя война между Англией и Францией. Климат стал холоднее, а сельское хозяйство пришло в упадок. Производство монет из ценных металлов в это время уменьшилось приблизительно на 80%. Многие явления, присущие Средневековью, такие, как вассальноленная система и папская власть, пришли в упадок. В то же время, этот период характеризует расцвет искусства и культурной жизни. В XIV веке в Италии началось возрождение античной культуры, или Ренессанс, влияние которого простиралось далеко через Альпы. Изобретение печатного искусства открыло невиданные горизонты для развития европейской науки и литературы. После открытия Америки были проложены новые торговые пути и, вследствие этого, у Европы появились предпосылки добиться экономического господства во всем мире.', 'Средние века'),
(10, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Renessanse.jpg', 'Возрождение', 'По-другому ренессанс', 'Новая культурная парадигма возникла вследствие кардинальных изменений общественных отношений в Европе.
Особое значение в становлении Возрождения имело падение Византийского государства, и бежавшие в Европу византийцы, взявшие с собой свои библиотеки и произведения искусства, содержавшие множество античных источников, неизвестных средневековой Европе, а также являвшиеся носителями античной культуры, в Византии никогда не забывавшейся.
Рост городов-республик привёл к росту влияния сословий, не участвовавших в феодальных отношениях: мастеров и ремесленников, торговцев, банкиров. Всем им была чужда иерархическая система ценностей, созданная средневековой, во многом церковной культурой, и её аскетичный, смиренный дух. Это привело к появлению гуманизма — общественно-философского движения, рассматривавшего человека, его личность, его свободу, его активную, созидающую деятельность как высшую ценность и критерий оценки общественных институтов.
В городах стали возникать светские центры науки и искусства, деятельность которых находилась вне контроля церкви. Новое мировоззрение обратилось к античности, видя в ней пример гуманистических, неаскетичных отношений. Изобретение в середине XV века книгопечатания сыграло огромную роль в распространении античного наследия и новых взглядов по всей Европе.
Возрождение возникло в Италии, где первые его признаки были заметны ещё в XIII и XIV веках, но оно твёрдо установилось только с 1420-х годов.', 'Новое время'),
(11, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Reformation.jpg', 'Реформация в Речи Посполитой', 'Наш край', 'Сторонники реформации требовали изменений в польской церкви. Реформация существовала с 1520-х годов до второй половины XVII века, когда её окончательно победила контрреформация.
Реформация была в основном уделом высших и средних слоёв общества, главным образом в городах северо-запада. Реформация не оказала большого влияния на сельское население, исключение составляли только жители западного пограничья. Движению не хватало могущественных покровителей. Магнаты рассматривали принадлежность к той или иной конфессии как своё частное дело, как часть дворянских вольностей, хотя на территории Литвы были такие магнаты, как Николай Чёрный Радзивилл и Ян Кишка, которые финансировали реформацию. Ни в одной из областей реформаты не угрожали позициям католической церкви.
В Речи Посполитой существовали три различных реформатских течения.
— На западе и в Великой Польше распространилось лютеранство, в основном усилиями снизу.
— Кальвинизм, напротив, распространялся при поддержке магнатов сверху в Малой Польше, Великом княжестве Литовском и Галиции, нередко путём административного перевода церковных приходов из католичества в кальвинизм.
— Наконец, из кальвинизма выделилось движение польских братьев, которое противники называли «арианством».
Реформация оказала значительное влияние на развитие польского языка, культуры и литературы, привела к оживлению науки и просвещения, развитию образования и книгоиздательства. Деятелями реформации были известные писатели, богословы и учёные.', 'Новое время'),
(12, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Contrreformation.jpg', 'Контрреформация в Речи Посполитой', 'Наш край', 'Со второй половины XVI в. католическая церковь начала борьбу за восстановление позиций католичества. Это движение получило название Контрреформации. Одним из главных орудий борьбы с Реформацией стал орден иезуитов, созданный в 1534 г. и утвержденный папой римским в 1540 г. Представители этого ордена были приглашены в ВКЛ в 1569 г. Главным идеологом Контрреформации был иезуит Петр Скарга. Активными ее сторонниками являлись Стефан Баторий и Сигизмунд III Лаза. Они способствовали укреплению католической церкви, поощряли деятельность католических монашеских орденов. В результате мероприятий Контрреформации упрочилось экономическое могущество и политическое влияние католической церкви, произошла клерикализация культуры. Мероприятия Контрреформации в Речи Посполитой были направлены не только против протестантизма, но и против православия. В рамках Контрреформации проведено объединение православной церкви Беларуси и Украины с католической, оформленное на Брестском церковном соборе в 1569 г. Контрреформация в Речи Посполитой фактически продолжалась до 30-х г. XVIII в.', 'Новое время'),
(13, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Industriation.jpg', 'Индустриализация', 'Эволюция мира', 'Индустриализация — процесс ускоренного социально-экономического перехода от традиционного этапа развития к индустриальному, с преобладанием промышленного производства в экономике. Этот процесс связан с развитием новых технологий, особенно в таких отраслях, как энергетика и металлургия. В ходе индустриализации общество также претерпевает некоторые изменения, меняется его мировосприятие. Позитивное отношение к труду в сочетании со стремлением как можно быстрее использовать новые технологии и научные открытия также дают вклад в ускоренный рост производства и доходов населения. В результате образуется все более широкий, в конечном счете, мировой рынок продукции и услуг всех видов, что в свою очередь стимулирует инвестиции и дальнейший экономический рост.
Индустриализация — создание крупной, технически развитой промышленности, значительное увеличение доли промышленности в экономике.
Сроки и темпы индустриализации в разных странах могут быть неодинаковыми. Первой страной, где произошла промышленная революция, стала Великобритания (в середине XIX века). Франция стала индустриальной в начале 20-х годов XX века. В Российской империи индустриализация началась с конца XIX — до начала XX вв. К концу XX века одним из наиболее экономически успешных регионов стала Восточная Азия, в особенности Гонконг.', 'Новейшее время'),
(14, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Capitalism.jpg', 'Капитализм', 'Экономическая система', 'Капитализм — экономическая система производства и распределения, основанная на частной собственности, юридическом равенстве и свободе предпринимательства. Главным критерием для принятия экономических решений является стремление к увеличению капитала, к получению прибыли.
Понятие «капитализм» — это экономическая абстракция, в которой выделены характерные черты экономики на определённом этапе её развития и при этом отброшены менее значимые. Реальная экономика конкретных стран никогда не основывалась только на частной собственности и не предоставляла полной свободы предпринимательства. Всегда в той или иной мере присутствовали несвойственные капитализму черты — сословные привилегии; государственная собственность; ограничения на владение имуществом, в том числе ограничения на размеры недвижимости или земельных участков; таможенные барьеры; антимонопольные правила и т. п. Часть из них являются наследием предыдущих эпох, часть — следствием развития самого капитализма.
Капиталистическое разделение труда не имеет никакого отношения к взаимодополнительности задач, индивидов и социальных групп. Процессы нарастания имущественного неравенства, концентрации капитала в руках узкой группы лиц и усиление влияния корпоративного сектора на институты демократии, прекаризации значительных групп западного общества, по мнению исследователей и части западной политической элиты, создаёт угрозу стабильности и самому существованию западной демократии.', 'Новейшее время'),
(15, 'D:\University\coursach\MyLove\MyLove\DesignElements\Pictures\Globalization.jpg', 'Глобализация', 'Процесс интеграции', 'Глобализация — процесс всемирной экономической, политической, культурной и религиозной интеграции и унификации.
Глобализация является характерной чертой процессов изменения структуры мирового хозяйства, понимаемого как совокупность национальных хозяйств, связанных друг с другом системой международного разделения труда, экономических и политических отношений, путём включения в мировой рынок и тесного переплетения экономики на основе транснационализации и регионализации. На этой базе происходит формирование единой мировой сетевой рыночной экономики — геоэкономики и её инфраструктуры, уменьшение влияния государственного суверенитета, которое являлось главным действующим лицом международных отношений на протяжении многих веков. Процесс глобализации есть следствие эволюции государственно оформленных рыночных систем.
Основным следствием этого является мировое разделение труда, миграция (и, как правило, концентрация) в масштабах всей планеты капитала, рабочей силы, производственных ресурсов, стандартизация законодательства, экономических и технологических процессов, а также сближение и слияние культур разных стран. Это объективный процесс, который носит системный характер, то есть охватывает все сферы жизни общества. В результате глобализации мир становится более связанным и более зависимым от всех его субъектов. Происходит как увеличение количества общих для группы государств проблем, так и расширение числа и типов интегрирующихся субъектов.', 'Новейшее время')
